              maxlength="4"
              name="credit-cvc"
              pattern="\d*"
              placeholder="CVC"
              type="tel"
            />
            <input
              class="cc-name"
              maxlength="20"
              name="credit-name"
              pattern="\d*"
              placeholder="Your name"
              type="text"
            />
          </form>
          <input
            class="form-submit"
            type="submit"
            value="Pay"
          
          />
        </div>
      </div>
    </div>
  );
};
export default Home;
