import React, { useState } from "react";
import validator from "validator";

const App = () => {
  const [errorMessage, setErrorMessage] = useState("");

  const validateCreditCard = (value) => {
    if (validator.isCreditCard(value)) {
      setErrorMessage("Valid CreditCard Number");
    } else {
      setErrorMessage("Enter valid CreditCard Number!");
    }
  };

  function stripeCardNumberValidation(cardNumber) {
    const regexPattern = {
    MASTERCARD: /^5[1-5][0-9]{1,}|^2[2-7][0-9]{1,}$/,
    VISA: /^4[0-9]{2,}$/,
    AMERICAN_EXPRESS: /^3[47][0-9]{5,}$/,
    DISCOVER: /^6(?:011|5[0-9]{2})[0-9]{3,}$/,
    DINERS_CLUB: /^3(?:0[0-5]|[68][0-9])[0-9]{4,}$/,
    JCB: /^(?:2131|1800|35[0-9]{3})[0-9]{3,}$/
    };
    for (const card in regexPattern) {
    if (cardNumber.replace(/[^\d]/g, "").match(regexPattern[card])) {
    if (cardNumber) {
    return cardNumber &&
    /^[1-6]{1}[0-9]{14,15}$/i.test(
    cardNumber.replace(/[^\d]/g, "").trim()
    )
    ? ""
    : "Enter a valid Card";
    }
    }
    }
    return "Enter a valid Card";
    }
    //For the Card Imgages
     const CARDICON = {
    VISA: VISA_ICON,
    MASTERCARD: MASTER_CARD_ICON,
    DISCOVER: DISCOVER_ICON,
    AMERICAN_EXPRESS: AMERICAN_EXPRESS_ICON,
    DINERS_CLUB: DINERS_CLUB_ICON,
    JCB: JCB_ICON
    };

  return (
    <div
      style={{
        marginLeft: "200px",
      }}
    >
      <pre>
        <h2>Validating CreditCard in ReactJS</h2>
        <span>Enter CreditCard: </span>
        <input
          type="text"
          onChange={(e) => validateCreditCard(e.target.value)}
        ></input>{" "}
        <br />
        <span
          style={{
            fontWeight: "bold",
            color: "red",
          }}
        >
          {errorMessage}
        </span>
      </pre>
    </div>
  );
};

export default App;
