            onChange={this.handleInputChange}
            onFocus={this.handleInputFocus}
          />
          ...
        </form>

      <h1>Please fill  out this form for pay</h1>
      <div className={s.creditcard}>
        <div className = {s.inputforpay}>
    
          <form className= {s.option} disabled="disabled" id="credit" ref = {newCard}>
            <input
              class="cc-number"
              maxlength="19"
              minlength="19"
              name="credit-number"
              pattern="\d*"
              placeholder="Card Number"
              type="tel"
            />
         <div class="border"></div>
            <input
              class="cc-expires"
              maxlength="5"
              name="credit-validthure"
              pattern="\d*"
              placeholder="MM / YY"
              type="tel"
           
            />
            <input
              class="cc-cvc"
              maxlength="4"
              name="credit-cvc"
              pattern="\d*"
              placeholder="CVC"
              type="tel"
            />
            <input
              class="cc-name"
              maxlength="20"
              name="credit-name"
              pattern="\d*"
              placeholder="Your name"
              type="text"
    
            />
          </form>
          <input class="form-submit" type="submit" value="Pay"  onclick={props.addCard} />
        </div>
      </div>
    </div>
  );

  }
export default Home;
