import React from "react";
import s from "./Home.module.css";
const Home = (props) => {
  
  let addPosts = () => {
    let newPosts =  newCard.current.value;
  };
  let newCard = React.createRef();

  return (
    <div className={s.homepagestyle}>
      <h1>Please fill  out this form for pay</h1>
      <div className={s.creditcard}>
        <div className = {s.inputforpay}>
    
          <form className= {s.option} disabled="disabled" id="credit" ref = {newCard}>
            <input
              class="cc-number"
              maxlength="19"
              name="credit-number"
              pattern="\d*"
              placeholder="Card Number"
              onclick="GetCardType(document.form.credit-number)"
              type="tel"
            />
            <input
              class="cc-expires"
              maxlength="5"
              name="credit-validthure"
              pattern="\d*"
              placeholder="MM / YY"
              type="tel"
           
            />
            <input
              class="cc-cvc"
              maxlength="4"
              name="credit-cvc"
              pattern="\d*"
              placeholder="CVC"
              type="tel"
            />
            <input
              class="cc-name"
              maxlength="20"
              name="credit-name"
              pattern="\d*"
              placeholder="Your name"
              type="text"
            />
          </form>
          <input class="form-submit" type="submit" value="Pay" onClick = {props.addCard} />
        </div>
      </div>
    </div>
  );

  function GetCardType(number)
{
    // visa
    var re = new RegExp("^4");
    if (number.match(re) != null)
        return "Visa";

    // Mastercard 
    // Updated for Mastercard 2017 BINs expansion
     if (/^(5[1-5][0-9]{14}|2(22[1-9][0-9]{12}|2[3-9][0-9]{13}|[3-6][0-9]{14}|7[0-1][0-9]{13}|720[0-9]{12}))$/.test(number)) 
        return "Mastercard";

    // AMEX
    re = new RegExp("^3[47]");
    if (number.match(re) != null)
        return "AMEX";

    // Discover
    re = new RegExp("^(6011|622(12[6-9]|1[3-9][0-9]|[2-8][0-9]{2}|9[0-1][0-9]|92[0-5]|64[4-9])|65)");
    if (number.match(re) != null)
        return "Discover";

    // Diners
    re = new RegExp("^36");
    if (number.match(re) != null)
        return "Diners";

    // Diners - Carte Blanche
    re = new RegExp("^30[0-5]");
    if (number.match(re) != null)
        return "Diners - Carte Blanche";

    // JCB
    re = new RegExp("^35(2[89]|[3-8][0-9])");
    if (number.match(re) != null)
        return "JCB";

    // Visa Electron
    re = new RegExp("^(4026|417500|4508|4844|491(3|7))");
    if (number.match(re) != null)
        return "Visa Electron";

    return "";
}
};
export default Home;
