import React from "react";
import s from "./Home.module.css";
import { clear } from "./../index";
import { GetCardType } from "./../index";
import Cards from "react-credit-cards";

import "react-credit-cards/es/styles-compiled.css";
import { useState } from "react";
import validator from "validator";

const Home = (props) => {
  this.state = {
    cvc: '',
    expiry: '',
    focus: '',
    name: '',
    number: '',
  };

 const handleInputFocus = (e) => {
    this.setState({ focus: e.target.name });
  }
  
 const handleInputChange = (e) => {
    const { name, value } = e.target;
    
    this.setState({ [name]: value });
  }



  return (
    <div className={s.homepagestyle}>
      <h1>Please fill out this form for pay</h1>
      <div className={s.creditcard}>
        <div className={s.inputforpay}>
          <form
            className={s.option}
            disabled="disabled"
            id="credit"
          >
            <input
              class="cc-number"
              maxlength="19"
              minlength="19"
              name="credit-number"
              pattern="\d*"
              placeholder="Card Number"
              type="tel"
           //   onChange={this.numberChange.bind(this)}
              
            />
            <span>
      
            </span>
            <div class="border"></div>
            <input
              class="cc-expires"
              maxlength="5"
              name="credit-validthure"
              pattern="\d*"
              placeholder="MM / YY"
              type="tel"
             // onChange={this.dayChange.bind(this)}
            />
            <input
              class="cc-cvc"
              maxlength="4"
              name="credit-cvc"
              pattern="\d*"
              placeholder="CVC"
              type="tel"
              //onChange={this.ccvChange.bind(this)}
            />
            <input
              class="cc-name"
              maxlength="20"
              name="credit-name"
              pattern="\d*"
              placeholder="Your name"
              type="text"
              //onChange={this.nameChange.bind(this)}

            />
          </form>
          <input
            class="form-submit"
            type="submit"
            value="Pay"
                  />
        </div>
      </div>
    </div>
  );
}
export default Home;
